import React, { Fragment, Component } from 'react'
import { Button } from 'react-bootstrap'
import PropTypes from 'prop-types'
import ColorScheme from '../../libs/ColorScheme.js'
import ColorSchemeList from '../../libs/ColorSchemeList.js'
import ColorSchemeRow from '../ColorSchemeRow/ColorSchemeRow.jsx'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes } from '@fortawesome/free-solid-svg-icons'
import './SettingsModal.css'
import Settings from '../../libs/Settings.js'

class SettingsModal extends Component {
    constructor (props) {
        super(props)

        this.state = {
            savedSettings: this.props.settings,
            settings: this.props.settings
        }

        this.settingsRestored = this.settingsRestored.bind(this)
        this.settingsHaveChanged = this.settingsHaveChanged.bind(this)
        this.settingsAreDefault = this.settingsAreDefault.bind(this)
        this.cancelClicked = this.cancelClicked.bind(this)
    }

    settingsRestored () {
        this.setState({
            settings: new Settings()
        })
    }

    settingsHaveChanged () {
        return !this.state.settings.isEqualTo(this.state.savedSettings)
    }

    settingsAreDefault () {
        return this.state.settings.isDefault()
    }

    cancelClicked () {

        if (!this.settingsHaveChanged()) {
            this.props.settingsBack(this.state.settings)
        } else {
            let result = window.confirm("Settings have changed, are you sure you want to leave and discard changes?")
            if (result) {
                this.props.settingsBack(this.state.savedSettings)
            }
        }
    }

    render () {
        const { savedSettings, settings } = this.state
        console.log("Should show modal? " + this.props.shouldShow)
        return (
            <div style={{display: `${this.props.shouldShow ? "block" : "none"}`}} className="settings-modal-wrapper">
                <FontAwesomeIcon className="settings-modal-cancel" icon={faTimes} onClick={() => this.cancelClicked()}/>
                <div className="settings-modal-header">Crossword Settings</div>
                <div className="settings-modal-body">
                    <div className="settings-modal-section-title">Color Schemes</div>
                    {ColorSchemeList.colorSchemes.map( (c,i) =>
                        <div className="color-scheme-row" 
                            style={{backgroundColor : `${c.key === settings.colorScheme.key ? "#D0D0D0" : "white"}`}}>
                            <div className="color-scheme-row-part">
                                <div className="color-scheme-row-name">{c.displayName}</div>
                            </div>
                            <div className="color-scheme-row-part">
                                <ColorSchemeRow className="color-scheme-row-graphic" colorScheme={c} width={170} height={30} />
                            </div>
                        </div>
                    )}
                </div>
                <div className="settings-modal-footer">
                    <Button style={{disabled: `${!this.settingsHaveChanged()}`}} className="settings-save-button">Save Settings</Button>
                    <Button style={{disabled: `${this.settingsAreDefault()}`}} className="settings-restore-button">Restore Defaults</Button>
                </div>
            </div>
        )
    }
}

SettingsModal.propTypes = {
    shouldShow: PropTypes.bool.isRequired,
    settings: PropTypes.object.isRequired,
    settingsSave: PropTypes.object.isRequired,
    settingsBack: PropTypes.func.isRequired
}

export default SettingsModal
