import React, { Fragment, Component } from 'react'
import PropTypes from 'prop-types'
import './CrosswordPage.css'
import pause from '../../images/pause.png'

class CrosswordPage extends Component {
    constructor (props) {
        super(props)

        this.state = {
            windowWidth: window.innerHeight,
            checkClicked: false,
            revealClicked: false,
            id: this.props.crossword.id,
            title: this.props.crossword.title,
            date: this.props.crossword.date,
            difficulty: this.props.crossword.difficulty,
            numbered: this.props.crossword.numbered,
            grid: this.props.crossword.grid,
            acrossClues: this.props.crossword.acrossClues,
            downClues: this.props.crossword.downClues,
            clueRefMap: this.props.crossword.clueRefMap
        }

        this.handleWindowResize = this.handleWindowResize.bind(this)
        this.handleGenericClick = this.handleGenericClick.bind(this)

        this.checkParentClicked = this.checkParentClicked.bind(this)
        this.checkParentUnclicked = this.checkParentUnclicked.bind(this)

        this.checkSquareClicked = this.checkSquareClicked.bind(this)
        this.checkWordClicked = this.checkWordClicked.bind(this)
        this.checkPuzzleClicked = this.checkPuzzleClicked.bind(this)

        this.revealParentClicked = this.revealParentClicked.bind(this)
        this.revealParentUnclicked = this.revealParentUnclicked.bind(this)

        this.revealSquareClicked = this.revealSquareClicked.bind(this)
        this.revealWordClicked = this.revealWordClicked.bind(this)
        this.revealPuzzleClicked = this.revealPuzzleClicked.bind(this)
    }

    componentDidMount () {
        window.addEventListener('resize', this.handleWindowResize)
        window.addEventListener('mousedown', this.handleGenericClick)
    }

    componentWillUnmount () {
        window.removeEventListener('resize', this.handleWindowResize)
        window.addEventListener('mousedown', this.handleGenericClick)

        // save state of grid
    }

    handleWindowResize () {
        this.setState({
            windowWidth: window.innerHeight
        })
    }

    handleGenericClick () {
        this.checkAndRevealUnclicked()
    }

    checkAndRevealUnclicked () {
        this.checkParentUnclicked()
        this.revealParentUnclicked()
    }

    checkParentClicked () {
        this.setState({
            checkClicked: true
        })
        document.getElementsByClassName('crossword-check-parent')[0].style.transform = "translateX(-240px)"
        let checkElements = document.getElementsByClassName('crossword-check')
        for (let element of checkElements) {
            element.style.display = "inline-block"
        }
    }

    checkParentUnclicked () {
        if (this.state.checkClicked) {
            this.setState({
                checkClicked: false
            })
            document.getElementsByClassName('crossword-check-parent')[0].style.transform = "translateX(240px);"
            let checkElements = document.getElementsByClassName('crossword-check')
            for (let element of checkElements) {
                element.style.display = "none"
            }
        }
    }

    revealParentClicked () {
        this.setState({
            revealClicked: true
        })
        document.getElementsByClassName('crossword-reveal-parent')[0].style.transform = "translateX(-240px)"
        let revealElements = document.getElementsByClassName('crossword-reveal')
        for (let element of revealElements) {
            element.style.display = "inline-block"
        }
    }

    revealParentUnclicked () {
        if (this.state.revealClicked) {
            this.setState({
                revealClicked: false
            })
            document.getElementsByClassName('crossword-reveal-parent')[0].style.transform = "translateX(240px);"
            let revealElements = document.getElementsByClassName('crossword-reveal')
            for (let element of revealElements) {
                element.style.display = "none"
            }
        }
    }

    checkSquareClicked () {

    }

    checkWordClicked () {

    }

    checkPuzzleClicked () {

    }

    revealSquareClicked () {

    }

    revealWordClicked () {

    }

    revealPuzzleClicked () {

    }

    render () {
        const { id, title, date, difficulty, numbered, grid, acrossClues, downClues, clueRefMap } = this.state
        let screenWidth = window.innerWidth
        let clueWidth = "" + (screenWidth - 790 - 2) + "px"
        return (
            <Fragment>
                <div className="crossword-page-wrapper">
                    <div className="crossword-page-back-button" onClick={() => { this.props.backSelected() }}>
                        {'<'} Save progress and go back
                    </div>
                    <div className="crossword-page-heading">
                        <div className="crossword-page-title">{title}</div>
                        <div className="crossword-page-author">By Nathan Majumder</div>
                    </div>
                    <div className="crossword-page-controls">
                        <div className="crossword-timer-pause">
                            <div className="crossword-timer">13:41</div><img className="crossword-pause-button" src={pause}/>
                        </div>
                        <div className="crossword-check-and-reveal">
                            <div className="crossword-check crossword-check-or-reveal" style={{display: "none"}}>Square</div>
                            <div className="crossword-check crossword-check-or-reveal" style={{display: "none"}}>Word</div>
                            <div className="crossword-check crossword-check-or-reveal" style={{display: "none"}}>Puzzle</div>
                            <div className="crossword-check-parent crossword-check-or-reveal" 
                                onClick={() => { this.checkParentClicked() }}>Check</div>
                            <div className="crossword-reveal crossword-check-or-reveal" style={{display: "none"}}>Square</div>
                            <div className="crossword-reveal crossword-check-or-reveal" style={{display: "none"}}>Word</div>
                            <div className="crossword-reveal crossword-check-or-reveal" style={{display: "none"}}>Puzzle</div>
                            <div className="crossword-reveal-parent crossword-check-or-reveal"
                                onClick={() => { this.revealParentClicked() }}>Reveal</div>
                        </div>
                    </div>
                    <div className="crossword-board-and-clues-wrapper">
                        <div className="crossword-board">
                            {grid.map( row =>
                                <div className={grid.length === 15 ? "crossword-row-15" : "crossword-row-21"}>
                                    {row.map( square => 
                                        <div className={`crossword-square 
                                                ${square.value === '_' ? "crossword-square-black " : ""}
                                                ${grid.length === 15 ? "crossword-square-15" : "crossword-square-21"}`}>
                                            {square.value === '?' || square.value === '_' ? '' : square.value}
                                        </div>
                                    )}
                                </div>

                            )}
                        </div>
                        <div className="crossword-clue-section" style={{width : clueWidth}}>
                            <div className="crossword-clue-list">
                                <div className="crossword-clue-list-title">
                                    Across
                                </div>
                            </div>
                            <div className="crossword-clue-list">
                                <div className="crossword-clue-list-title">
                                    Down
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </Fragment>
        )
    }
}

CrosswordPage.propTypes = {
    crossword: PropTypes.object.isRequired,
    backSelected: PropTypes.func.isRequired
}

export default CrosswordPage
