import React, { Fragment, Component } from 'react'
import PropTypes from 'prop-types'
import '../css/Timer.css'

class Timer extends Component {
    constructor (props) {
        super(props)

        this.state = {
            elapsed: 0
        }
    }

    componentDidMount () {
        if (!this.props.isPaused) {
            this.timerInterval = setInterval(() => {
                this.setState(({ elapsed }) => ({
                    elapsed: elapsed + 1
                }))
            }, 1000);
        }
    }

    componentWillUnmount () {
        clearInterval(this.timerInterval)
    }

    render () {
        const { elapsed } = this.state
        let seconds = elapsed
        let minutes = 0
        let hours = 0
        if (seconds >= 60) {
            minutes = Math.floor(seconds / 60)
            seconds = seconds % 60
            if (minutes >= 60) {
                hours = Math.floor(minutes / 60)
                minutes = minutes % 60
            }
        }

        let secondStr = seconds < 10 ? "0" + seconds : "" + seconds
        let minuteStr = "" + minutes
        if (hours > 0) {
            minuteStr = minutes < 10 ? "0" + minutes : "" + minutes
        }
        let hourStr = "" + hours

        return (
            <div className="crossword-timer">
                <div className="timer-part" style={{display: `${hours === 0 ? "none" : ""}`}}>
                    {hourStr.split("").map( h =>
                        <div className="timer-digit">{h}</div>
                    )}
                </div>
                <div className="timer-colon">{hours > 0 ? ":" : ""}</div>
                <div className="timer-part">
                    {minuteStr.split("").map( m =>
                        <div className="timer-digit">{m}</div>
                    )}
                </div>
                <div className="timer-colon">:</div>
                <div className="timer-part">
                    {secondStr.split("").map( s =>
                        <div className="timer-digit">{s}</div>
                    )}
                </div>
            </div>
        )
    }
}

Timer.propTypes = {
    isPaused: PropTypes.bool.isRequired
}

export default Timer
