import React, { Fragment, Component } from 'react'
import PropTypes from 'prop-types'
import '../css/CrosswordPage.css'

class Timer extends Component {
    constructor (props) {
        super(props)

        this.state = {
            seconds: 0
        }
    }

    componentDidMount () {
        if (!this.props.isPaused) {
            this.setInterval(() => {
                this.setState(({ seconds }) => ({
                    seconds: seconds + 1
                }))
            }, 1000);
        }
    }

    render () {
        const { seconds } = this.state
        let minutes = 0
        let hours = 0
        if (seconds >= 60) {
            minutes = seconds / 60
            seconds = seconds % 60
            if (minutes >= 60) {
                hours = minutes / 60
                minutes = minutes % 60
            }
        }

        return (
            <div className="crossword-timer">{hours > 0 ? `${hours + ":" + minutes + ":" + seconds}` : `${minutes + ":" + seconds}`}</div>
        )
    }
}

Timer.propTypes = {
    isPaused: PropTypes.bool.isRequired
}

export default Timer
