import React, { Fragment, Component } from 'react'
import PropTypes from 'prop-types'
import Crossword from '../Crossword/Crossword.jsx'
import axios from 'axios'

class CrosswordApp extends Component {
    constructor (props) {
        super(props)

        this.state = {
            crosswords: []
        }

        this.loadCrosswords = this.loadCrosswords.bind(this)
    }

    async loadCrosswords () {
        let response
        let requestSuccess = false
        try {
            console.log('Sending call...')

            response = await this.props.api().listVersionsWithPrefix()
            console.log(response)
            requestSuccess = response.status === 200
        } catch (error) {
            requestSuccess = false
        }

        if (requestSuccess) {
            this.setState({
                crosswords: response.data
            })
        }
    }

    async componentWillMount () {
        await this.loadCrosswords()
    }

    render () {
        const { crosswords } = this.state
        return (
            <Fragment>
                {crosswords.map(crossword =>
                    <Crossword
                        api={this.props.api}
                        crossword={crossword}
                    />
                )}
            </Fragment>
        )
    }
}

CrosswordApp.propTypes = {
    api: PropTypes.func.isRequired
}

export default CrosswordApp
