import axios from 'axios'

export default function api () {
    const baseUrl = 'api'
    return {
        createUser: async (user, pass) => {
            return axios.post(`/api/user/create`, {username: user, password: pass})
        },
        getAllUsers: async () => {
            return axios.get(`/api/user/all`)
        },
        getUser: async (id) => {
            return axios.get(`/api/user/` + id)
        },
        getAllCrosswords: async () => {
            return axios.get(`/api/crossword/all`)
        },
        getCrossword: async (id, userid) => {
            return axios.get(`/api/crossword/` + id + `/` + userid)
        },
        updateCrossword: async (id, userid) => {
            return axios.put(`/api/crossword/` + id + `/` + userid)
        }
    }
}
