import React, { Fragment } from 'react'
import PropTypes from 'prop-types'

class CrosswordApp extends React.Component {
    constructor (props) {
        super(props)

        this.state = {
            crosswords: []
        }

        this.loadCrosswords = this.loadCrosswords.bind(this)
    }

    async loadCrosswords () {
        let response
        let requestSuccess = false
        try {
            response = await this.props.api().listVersionsWithPrefix()
            requestSuccess = response.status === 200
        } catch (error) {
            requestSuccess = false
        }

        if (requestSuccess) {
            this.setState({
                crosswords: response.data
            })
        }
    }

    async componentDidMount () {
        await this.loadCrosswords()
    }

    render () {
        const { crosswords } = this.state
        return (
            <Fragment>
                {crosswords.map(crossword => {
                    <Crossword
                        crossword={crossword}/>
                })}
            </Fragment>
        )
    }
}

CrosswordApp.propTypes = {
    api: PropTypes.func.isRequired
}

export default CrosswordApp
