import React, { Fragment, Component } from 'react'
import PropTypes from 'prop-types'
import { Stage, Layer, Shape, Line, Text } from 'react-konva';

class ColorSchemeRow extends Component {

    render () {
        const width = this.props.width
        const height = this.props.height
        console.log(width + " " + height)
        return (
            <Stage width={width} height={height}>
                <Layer>
                    <Shape
                        sceneFunc={(context, shape) => {
                            context.beginPath()
                            context.moveTo(0,0)
                            context.lineTo(0, height)
                            context.lineTo((width / 3) - (height * 3 / 3), height)
                            context.lineTo((width / 3) + (height * 3 / 3), 0)
                            context.closePath()
                            // (!) Konva specific method, it is very important
                            context.fillStrokeShape(shape)
                        }}
                        fill={this.props.colorScheme.colors[3]}
                        stroke="black"
                        strokeWidth={1}
                    />
                    <Shape
                        sceneFunc={(context, shape) => {
                            context.beginPath()
                            context.moveTo(0,0)
                            context.lineTo(0, height)
                            context.lineTo((width / 3) - (height * 3 / 8), height)
                            context.lineTo((width / 3) + (height * 3 / 8), 0)
                            context.closePath()
                            // (!) Konva specific method, it is very important
                            context.fillStrokeShape(shape)
                        }}
                        fill={this.props.colorScheme.colors[2]}
                        stroke="black"
                        strokeWidth={1}
                    />
                    <Shape
                        sceneFunc={(context, shape) => {
                            context.beginPath()
                            context.moveTo((width / 3) + (height * 3 / 8), 0)
                            context.lineTo((width * 2 / 3) + (height * 3 / 8), 0)
                            context.lineTo((width * 2 / 3) - (height * 3 / 8), height)
                            context.lineTo((width / 3) - (height * 3 / 8), height)
                            context.closePath()
                            // (!) Konva specific method, it is very important
                            context.fillStrokeShape(shape)
                        }}
                        fill={this.props.colorScheme.colors[1]}
                        stroke="black"
                        strokeWidth={1}
                    />
                    <Shape
                        sceneFunc={(context, shape) => {
                            context.beginPath()
                            context.moveTo((width * 2 / 3) + (height * 3 / 8), 0)
                            context.lineTo(width, 0)
                            context.lineTo(width, height)
                            context.lineTo((width * 2 / 3) - (height * 3 / 8), height)
                            context.closePath()
                            // (!) Konva specific method, it is very important
                            context.fillStrokeShape(shape)
                        }}
                        fill={this.props.colorScheme.colors[0]}
                        stroke="black"
                        strokeWidth={1}
                    />
                </Layer>
                <Layer>
                    <Text 
                        text={this.props.colorScheme.displayText}
                        x={50}
                        y={40}
                        fontSize={20}
                        align="center"
                        width={200}/>
                </Layer>
            </Stage>
        )
    }
}

ColorSchemeRow.propTypes = {
    colorScheme: PropTypes.object.isRequired,
    width: PropTypes.number.isRequired,
    height: PropTypes.number.isRequired
}

export default ColorSchemeRow
