import React, { Fragment, PureComponent } from 'react'
import PropTypes from 'prop-types'
import './CrosswordBoardApp.css'
import SquareSelection from '../../libs/SquareSelection.js'

class CrosswordBoardApp extends PureComponent {
    constructor (props) {
        super(props)

        let currentSelection = new SquareSelection()
        let i = 0
        while (this.props.crossword.numbered[0][i] === "_") {
            i++
        }
        currentSelection.coords = [0,i]
        currentSelection.direction = "Across"

        this.state = {
            windowWidth: window.innerWidth,
            currentSelection: currentSelection,
            id: this.props.crossword.id,
            numbered: this.props.crossword.numbered,
            grid: this.props.crossword.grid,
            acrossClues: this.props.crossword.acrossClues.sort((c1, c2) => (c1.number > c2.number) ? 1 : -1),
            downClues: this.props.crossword.downClues.sort((c1, c2) => (c1.number > c2.number) ? 1 : -1),
            clueRefMap: this.props.crossword.clueRefMap
        }

        this.handleWindowResize = this.handleWindowResize.bind(this)
        this.clueRowClicked = this.clueRowClicked.bind(this)
        this.boardSquareClicked = this.boardSquareClicked.bind(this)

        this.getSelectedSquares = this.getSelectedSquares.bind(this)
        this.getSquareBackgroundColor = this.getSquareBackgroundColor.bind(this)
        this.getClueBackgroundColor = this.getClueBackgroundColor.bind(this)
        this.getClueBorderColor = this.getClueBorderColor.bind(this)
    }

    componentDidUpdate () {
        console.log("Crossword Board App updated...")
    }

    componentDidMount () {
        console.log("Crossword Board App is Mounting...")
        window.addEventListener('resize', this.handleWindowResize)

    }

    componentWillUnmount () {
        console.log("Crossword Board App is Unmounting...")
        window.removeEventListener('resize', this.handleWindowResize)

        // save state of grid
    }

    handleWindowResize () {
        this.setState({
            windowWidth: window.innerWidth
        })
    }

    clueRowClicked (index, listType) {
        if (listType === "Across") {
            console.log('Clicked across clue with number: ' + this.state.acrossClues[index].number)
        } else {
            console.log('Clicked down clue with number: ' + this.state.downClues[index].number)
        }
    }

    boardSquareClicked (square) {
        console.log("Clicked square at indices " + square.rowCoord + "," + square.colCoord)
    }

    getSelectedSquares (selection) {
        const coords = selection.coords
        const direction = selection.direction
        let boardSquare = this.state.grid[coords[0]][coords[1]]
        let selectedCoordList = []
        if (direction === "Across") {
            let clue = this.state.acrossClues.find(clue => clue.number === boardSquare.acrossClueNum)
            let firstCoords = [clue.rowCoord, clue.colCoord]
            let wordLen = clue.answerLength
            for (let i = firstCoords[1]; i < firstCoords[1] + wordLen; i++) {
                selectedCoordList.push([firstCoords[0],i].toString())
            }
        } else {
            let clue = this.state.downClues.find(clue => clue.number === boardSquare.downClueNum)
            let firstCoords = [clue.rowCoord, clue.colCoord]
            let wordLen = clue.answerLength
            for (let i = firstCoords[0]; i < firstCoords[0] + wordLen; i++) {
                selectedCoordList.push([i,firstCoords[1]].toString())
            }
        }
        return selectedCoordList
    }

    getSquareBackgroundColor (square, selection, highlightedSquares) {
        if (square.value === "_") return "black"
        let thisSquareKey = [square.rowCoord, square.colCoord].toString()
        if (highlightedSquares.includes(thisSquareKey)) {
            if (thisSquareKey == selection.coords.toString()) {
                return this.state.colorScheme.colors[0]
            } else {
                return this.state.colorScheme.colors[1]
            }
        }
        return "white"
    }

    getClueBackgroundColor (clue, selection) {
        let selectedSquare = this.state.grid[selection.coords[0]][selection.coords[1]]
        if (clue.direction === "ACROSS" && clue.number === selectedSquare.acrossClueNum
                && selection.direction === "Across") {
            return this.state.colorScheme.colors[1]
        } else if (clue.direction === "DOWN" && clue.number === selectedSquare.downClueNum
                    && selection.direction === "Down") {
            return this.state.colorScheme.colors[1]
        }
        return "white"
    }

    getClueBorderColor (clue, selection) {
        let selectedSquare = this.state.grid[selection.coords[0]][selection.coords[1]]
        if ((clue.direction === "ACROSS" && clue.number === selectedSquare.acrossClueNum) ||
                (clue.direction === "DOWN" && clue.number === selectedSquare.downClueNum)) {
            return this.state.colorScheme.colors[1]
        }   
        return "white"
    }

    render () {
        console.log("Crossword board app is rendering...")
        const { windowWidth, currentSelection, id, numbered, grid, acrossClues, downClues, clueRefMap } = this.state
        colorScheme = this.props.colorScheme
        let clueWidthPx = "" + (windowWidth - 790 - 2) + "px"

        const boardPx = this.props.boardWidthPx
        const boardSize = grid.length
        const rowPx = boardPx / boardSize
        const squarePx = rowPx - 1

        const highlightedSquares = this.getSelectedSquares(currentSelection)

        return (
            <Fragment>
                <div className="crossword-board-and-clues-wrapper">
                    <div className="crossword-board" style={{ width : boardPx, height : boardPx }}>
                        {grid.map( (row, i) =>
                            <div key={i} style={{height : rowPx}}>
                                {row.map( (square, j) => 
                                    <div key={j} className={"crossword-square"} onClick={() => { this.boardSquareClicked(square) }}
                                            style={{width : squarePx, height : squarePx, 
                                                backgroundColor : this.getSquareBackgroundColor(square, currentSelection, highlightedSquares)}}>
                                        {square.value === '?' || square.value === '_' ? '' : square.value}
                                    </div>
                                )}
                            </div>

                        )}
                    </div>
                    <div className="crossword-clue-section" style={{width : clueWidthPx, height : boardPx}}>
                        <div className="crossword-clue-list">
                            <div className="crossword-clue-list-title">
                                Across
                            </div>
                            <div className="crossword-clue-scroll">
                                {acrossClues.map( (clue,i) =>
                                    <div key={i} className="crossword-clue-row"
                                        style={{backgroundColor : this.getClueBackgroundColor(clue, currentSelection), 
                                            borderLeftColor : this.getClueBorderColor(clue, currentSelection)}}
                                        onClick={() => { this.clueRowClicked(acrossClues.findIndex(other => other.number === clue.number), "Across") }}>
                                        <div className="crossword-clue-row-number">{clue.number}</div>
                                        <div className="crossword-clue-row-text">{clue.text}</div>
                                    </div>
                                )}
                            </div>
                        </div>
                        <div className="crossword-clue-list">
                            <div className="crossword-clue-list-title">
                                Down
                            </div>
                            <div className="crossword-clue-scroll">
                                {downClues.map( (clue,i) =>
                                    <div key={i} className="crossword-clue-row" 
                                        style={{backgroundColor : this.getClueBackgroundColor(clue, currentSelection), 
                                            borderLeftColor : this.getClueBorderColor(clue, currentSelection)}}
                                        onClick={() => { this.clueRowClicked(downClues.findIndex(other => other.number === clue.number), "Down") }}>
                                        <div className="crossword-clue-row-number">{clue.number}</div>
                                        <div className="crossword-clue-row-text">{clue.text}</div>
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </Fragment>
        )
    }
}

CrosswordBoardApp.propTypes = {
    crossword: PropTypes.object.isRequired,
    boardWidthPx: PropTypes.number.isRequired,
    colorScheme: PropTypes.array.isRequired
}

export default CrosswordBoardApp
