import React, { Fragment, Component } from 'react'
import PropTypes from 'prop-types'
import './CrosswordBoardApp.css'

class CrosswordBoardApp extends Component {
    constructor (props) {
        super(props)

        const boardPx = 630
        let size = this.props.crossword.grid.length
        let squarePx = boardPx % size === 0 ? boardPx / size : boardPx - (boardPx % size)

        this.state = {
            windowWidth: window.innerWidth,
            id: this.props.crossword.id,
            numbered: this.props.crossword.numbered,
            grid: this.props.crossword.grid,
            acrossClues: this.props.crossword.acrossClues.sort((c1, c2) => (c1.number > c2.number) ? 1 : -1),
            downClues: this.props.crossword.downClues,
            clueRefMap: this.props.crossword.clueRefMap
        }

        this.handleWindowResize = this.handleWindowResize.bind(this)
    }

    componentDidMount () {
        console.log("Crossword Board App is Mounting...")
        window.addEventListener('resize', this.handleWindowResize)
    }

    componentWillUnmount () {
        console.log("Crossword Board App is Unmounting...")
        window.removeEventListener('resize', this.handleWindowResize)

        // save state of grid
    }

    handleWindowResize () {
        this.setState({
            windowWidth: window.innerWidth
        })
    }

    render () {
        const { windowWidth, id, numbered, grid, acrossClues, downClues, clueRefMap } = this.state
        let clueWidth = "" + (windowWidth - 790 - 2) + "px"
        return (
            <Fragment>
                <div className="crossword-board-and-clues-wrapper">
                    <div className="crossword-board">
                        {grid.map( row =>
                            <div className={grid.length === 15 ? "crossword-row-15" : "crossword-row-21"}>
                                {row.map( square => 
                                    <div className={`crossword-square 
                                            ${square.value === '_' ? "crossword-square-black " : ""}
                                            ${grid.length === 15 ? "crossword-square-15" : "crossword-square-21"}`}>
                                        {square.value === '?' || square.value === '_' ? '' : square.value}
                                    </div>
                                )}
                            </div>

                        )}
                    </div>
                    <div className="crossword-clue-section" style={{width : clueWidth}}>
                        <div className="crossword-clue-list">
                            <div className="crossword-clue-list-title">
                                Across
                            </div>
                            <div className="crossword-clue-scroll">
                                {acrossClues.map( clue =>
                                    <div styleName="crossword-clue-row" onClick={() => { this.clueRowClicked(acrossClues.findIndex(otherclue => otherclue.number === clue.number)) }}>
                                        <span style={{fontWeight: "600"}}>{clue.number}</span> {clue.text}
                                    </div>
                                )}
                            </div>
                        </div>
                        <div className="crossword-clue-list">
                            <div className="crossword-clue-list-title">
                                Down
                            </div>
                            <div className="crossword-clue-scroll">

                            </div>
                        </div>
                    </div>
                </div>
            </Fragment>
        )
    }
}

CrosswordBoardApp.propTypes = {
    crossword: PropTypes.object.isRequired
}

export default CrosswordBoardApp
