import React, { Fragment, Component } from 'react'
import PropTypes from 'prop-types'
import '../css/CrosswordBoardApp.css'
import SquareSelection from '../libs/SquareSelection.js'
import CrosswordClueScroll from './CrosswordClueScroll.jsx'
import CrosswordKeyActions from '../libs/CrosswordKeyActions.js'
import api from '../libs/api.js'

class CrosswordBoardApp extends Component {
    constructor (props) {
        super(props)

        this.acrossClues = this.props.crossword.acrossClues.sort((c1, c2) => (c1.number > c2.number) ? 1 : -1)
        this.downClues = this.props.crossword.downClues.sort((c1, c2) => (c1.number > c2.number) ? 1 : -1)
        this.clueRefMap = this.props.crossword.clueRefMap
        this.board = this.props.crossword.board

        this.state = {
            currentSelection: this.board.selection,
            grid: this.board.grid,
            lastSavedGrid: this.board.grid
        }

        this.clueClicked = this.clueClicked.bind(this)
        this.boardSquareClicked = this.boardSquareClicked.bind(this)

        this.getSelectedSquares = this.getSelectedSquares.bind(this)
        this.getReferencedSquares = this.getReferencedSquares.bind(this)
        this.getSquaresOfClue = this.getSquaresOfClue.bind(this)
        this.getSquareBackgroundColor = this.getSquareBackgroundColor.bind(this)

        this.onKeyDown = this.onKeyDown.bind(this)

        this.crosswordIsComplete = this.crosswordIsComplete.bind(this)
    }

    componentDidMount () {
        console.log("Crossword Board App is Mounting...")
        document.addEventListener("keydown", this.onKeyDown, false);
    }

    componentWillUnmount () {
        console.log("Crossword Board App is Unmounting...")
        document.removeEventListener("keydown", this.onKeyDown, false);
        // save state of grid
    }

    async crosswordIsComplete (showNotComplete) {
        let response
        let requestSuccess = false
        try {
            console.log('Checking whether crossword is finished...')
            console.log(this.board)
            response = await api.crosswordIsComplete(this.props.crossword.id, "ME", this.board)
            console.log(response)
            requestSuccess = response.status === 200
        } catch (error) {
            requestSuccess = false
        }

        if (requestSuccess) {
            if (response.data) {
                this.props.crosswordFinished()
            } else {
                if (showNotComplete) {
                    this.props.crosswordUnfinished()
                }
            }
        }
    }

    onKeyDown (event) {
        console.log(event)
        // if special key pressed, allow default action
        if (event.ctrlKey || event.altKey || event.metaKey) {
            return
        }
        event.preventDefault()
        if (this.props.typingDisabled) {
            return
        }
        if (event.which === 13 || event.which === 9) {
            // enter or tab
            CrosswordKeyActions.tabOrEnter(this.state.grid, this.state.currentSelection, event.shiftKey, this.acrossClues, this.downClues)
        } else if (event.which === 8) {
            // delete
            CrosswordKeyActions.delete(this.state.grid, this.state.currentSelection)
        } else if (event.which === 37) {
            // left arrow
            CrosswordKeyActions.leftArrow(this.state.grid, this.state.currentSelection)
        } else if (event.which === 38) {
            // up arrow
            CrosswordKeyActions.upArrow(this.state.grid, this.state.currentSelection)
        } else if (event.which === 39) {
            // right arrow
            CrosswordKeyActions.rightArrow(this.state.grid, this.state.currentSelection)
        } else if (event.which === 40) {
            // down arrow
            CrosswordKeyActions.downArrow(this.state.grid, this.state.currentSelection)
        } else {
            let gridWasFull = CrosswordKeyActions.gridIsFull(this.state.grid)
            if (event.which >= 65 && event.which <= 90) {
                // a to z
                CrosswordKeyActions.alphaNumeric(this.state.grid, this.state.currentSelection, event.key.toUpperCase())
            } else if (event.which >= 48 && event.which <= 57) {
                // 0 to 9 or the symbols on the same keys
                CrosswordKeyActions.alphaNumeric(this.state.grid, this.state.currentSelection, event.key)
            } else if (event.which >= 186 && event.which <= 222) {
                // various symbols that we want to allow in case of special themed puzzle
                // disallow the underscore because it is reserved for black squares
                if (event.key === "_") {
                    return
                }
                CrosswordKeyActions.alphaNumeric(this.state.grid, this.state.currentSelection, event.key)
            }
            if (CrosswordKeyActions.gridIsFull(this.state.grid)) {
                this.crosswordIsComplete(!gridWasFull)
            }
        }
    }

    clueClicked (clue) {
        let coords = CrosswordKeyActions.getFirstEmptySpace(this.state.grid, clue)
        if (coords === null) {
            coords = [clue.rowCoord, clue.colCoord]
        }
        this.state.currentSelection.rowCoord = coords[0]
        this.state.currentSelection.colCoord = coords[1]
        this.state.currentSelection.direction = clue.direction
    }

    boardSquareClicked (square) {
        if (square.value === "_") {
            return
        }
        let selection = this.state.currentSelection
        if (square.rowCoord === selection.rowCoord && square.colCoord === selection.colCoord) {
            selection.direction = selection.direction === "Across" ? "Down" : "Across"
        } else {
            selection.rowCoord = square.rowCoord
            selection.colCoord = square.colCoord
        }
        this.setState({
            currentSelection: selection
        })
    }

    getSelectedSquares (selection) {
        const coords = selection.coords
        const direction = selection.direction
        let boardSquare = this.state.grid[coords[0]][coords[1]]
        let selectedCoordList = []
        if (direction === "Across") {
            let clue = this.acrossClues.find(clue => clue.number === boardSquare.acrossClueNum)
            selectedCoordList = selectedCoordList.concat(this.getSquaresOfClue(clue))
        } else {
            let clue = this.downClues.find(clue => clue.number === boardSquare.downClueNum)
            selectedCoordList = selectedCoordList.concat(this.getSquaresOfClue(clue))
        }
        return selectedCoordList
    }

    getReferencedSquares (selection) {
        const coords = selection.coords
        const direction = selection.direction
        let selectedCoordList = []
        if (direction === "Across") {
            let clueNum = this.state.grid[coords[0]][coords[1]].acrossClueNum
            let clueKey = "A" + clueNum
            if (clueKey in this.clueRefMap) {
                for (let key of this.clueRefMap[clueKey]) {
                    let clue
                    if (key.substring(0,1) === "A") {
                        clue = this.acrossClues.find(clue => clue.number.toString() === key.substring(1,key.length))
                    } else {
                        clue = this.downClues.find(clue => clue.number.toString() === key.substring(1, key.length))
                    }
                    selectedCoordList = selectedCoordList.concat(this.getSquaresOfClue(clue))
                }
            }
        } else {
            let clueNum = this.state.grid[coords[0]][coords[1]].downClueNum
            let clueKey = "D" + clueNum
            if (clueKey in this.clueRefMap) {
                for (let key of this.clueRefMap[clueKey]) {
                    let clue
                    if (key.substring(0,1) === "A") {
                        clue = this.acrossClues.find(clue => clue.number.toString() === key.substring(1,key.length))
                    } else {
                        clue = this.downClues.find(clue => clue.number.toString() === key.substring(1, key.length))
                    }
                    selectedCoordList = selectedCoordList.concat(this.getSquaresOfClue(clue))
                }
            }
        }
        return selectedCoordList
    }

    getSquaresOfClue (clue) {
        const [firstRow, firstCol] = [clue.rowCoord, clue.colCoord]
        let coordList = []
        if (clue.direction === "Across") {
            for (let i = firstCol; i < firstCol + clue.answerLength; i++) {
                coordList.push([firstRow, i].toString())
            }
        } else {
            for (let i = firstRow; i < firstRow + clue.answerLength; i++) {
                coordList.push([i, firstCol].toString())
            }
        }
        return coordList
    }

    getSquareBackgroundColor (square, selection, highlightedSquares, referencedSquares) {
        if (square.value === "_") return "black"
        let thisSquareKey = [square.rowCoord, square.colCoord].toString()
        if (highlightedSquares.includes(thisSquareKey)) {
            if (thisSquareKey === selection.coords.toString()) {
                return this.props.settings.colorScheme.colors[1]
            } else {
                return this.props.settings.colorScheme.colors[2]
            }
        } else if (referencedSquares.includes(thisSquareKey)) {
            return this.props.settings.colorScheme.colors[0]
        }
        return "white"
    }

    render () {
        const { currentSelection, grid } = this.state
        if (this.props.boardShouldBeSaved) {
            this.saveGridState()
        }

        let clueWidthPx = "" + (this.props.windowWidthPx - (this.props.boardWidthPx + 160) - 2) + "px"

        const boardPx = this.props.boardWidthPx
        const boardSize = this.props.crossword.board.grid.length
        const rowPx = boardPx / boardSize
        const squarePx = rowPx - 1

        const highlightedSquares = this.getSelectedSquares(currentSelection)
        const referencedSquares = this.getReferencedSquares(currentSelection)

        const acrossClue = this.acrossClues.find(c => c.number === grid[currentSelection.coords[0]][currentSelection.coords[1]].acrossClueNum)
        const downClue = this.downClues.find(c => c.number === grid[currentSelection.coords[0]][currentSelection.coords[1]].downClueNum)

        let squareNumberStyle = {
            fontSize: `${boardSize > 15 ? "6pt" : `${boardSize > 9 ? "9pt" : "16pt"}`}`,
            marginLeft: `${boardSize <= 9 ? "5px" : ""}`,
            marginTop: `${boardSize <= 9 ? "4px" : ""}`
        }
        let squareValueStyle = {
            width: squarePx,
            height: squarePx/2,
            marginTop: squarePx*3/10,
            fontSize: `${boardSize > 15 ? "13pt" : `${boardSize > 10 ? "17pt" : "44pt"}`}`
        }

        if (boardPx > 700) {
            // handle bigger board on large screens
            squareNumberStyle["fontSize"] = `${boardSize > 15 ? "10pt" : `${boardSize > 10 ? "13pt" : "22pt"}`}`
            squareValueStyle["fontSize"] = `${boardSize > 15 ? "20pt" : `${boardSize > 10 ? "24pt" : "64pt"}`}`
        }

        return (
            <Fragment>
                <div className="crossword-board-and-clues-wrapper">
                    <div className="crossword-board" style={{ width : boardPx, height : boardPx }}>
                        {grid.map( (row, i) =>
                            <div key={i} style={{height : rowPx}}>
                                {row.map( (square, j) => 
                                    <div key={j} className={"crossword-square"} onClick={() => { this.boardSquareClicked(square) }}
                                            style={{width : squarePx, height : squarePx, 
                                                backgroundColor : this.getSquareBackgroundColor(square, currentSelection, highlightedSquares, referencedSquares)}}>
                                        <div className="board-square-number" style={squareNumberStyle}>
                                            {grid[i][j].number > 0 ? grid[i][j].number : ""}</div>
                                        <div className="board-square-value" style={squareValueStyle}>
                                            {square.value === "" || square.value === '_' ? '' : square.value}
                                        </div>
                                    </div>
                                )}
                            </div>

                        )}
                    </div>
                    <div className="crossword-clue-section" style={{width : clueWidthPx, height : boardPx}}>
                        <CrosswordClueScroll 
                            listTitle={"Across"}
                            clueList={this.acrossClues}
                            selectedClue={acrossClue}
                            isMatchingDirection={currentSelection.direction === 'Across'}
                            clueClicked={this.clueClicked}
                            settings={this.props.settings}/>
                        <CrosswordClueScroll 
                            listTitle={"Down"}
                            clueList={this.downClues}
                            selectedClue={downClue}
                            isMatchingDirection={currentSelection.direction === 'Down'}
                            clueClicked={this.clueClicked}
                            settings={this.props.settings}/>
                    </div>
                </div>
            </Fragment>
        )
    }
}

CrosswordBoardApp.propTypes = {
    crossword: PropTypes.object.isRequired,
    typingDisabled: PropTypes.bool.isRequired,
    boardWidthPx: PropTypes.number.isRequired,
    windowWidthPx: PropTypes.number.isRequired,
    settings: PropTypes.object.isRequired,
    crosswordFinished: PropTypes.func.isRequired,
    crosswordUnfinished: PropTypes.func.isRequired
}

export default CrosswordBoardApp
