import React, { Fragment, Component } from 'react'
import PropTypes from 'prop-types'
import '../css/CrosswordBoardApp.css'
import SquareSelection from '../libs/SquareSelection.js'
import CrosswordClueScroll from './CrosswordClueScroll.jsx'

class CrosswordBoardApp extends Component {
    constructor (props) {
        super(props)

        this.numbered = this.props.crossword.numbered
        this.acrossClues = this.props.crossword.acrossClues.sort((c1, c2) => (c1.number > c2.number) ? 1 : -1)
        this.downClues = this.props.crossword.downClues.sort((c1, c2) => (c1.number > c2.number) ? 1 : -1)
        this.clueRefMap = this.props.crossword.clueRefMap

        let currentSelection = new SquareSelection()
        let i = 0
        while (this.props.crossword.numbered[0][i] === "_") {
            i++
        }
        currentSelection.coords = [0,i]
        currentSelection.direction = "Across"

        this.state = {
            windowWidth: window.innerWidth,
            currentSelection: currentSelection,
            grid: this.props.crossword.grid,
            clueScrollTop: 0
        }

        this.handleWindowResize = this.handleWindowResize.bind(this)
        this.acrossClueClicked = this.acrossClueClicked.bind(this)
        this.downClueClicked = this.downClueClicked.bind(this)
        this.boardSquareClicked = this.boardSquareClicked.bind(this)

        this.getSelectedSquares = this.getSelectedSquares.bind(this)
        this.getSquareBackgroundColor = this.getSquareBackgroundColor.bind(this)
    }

    componentDidUpdate () {
        console.log("Crossword Board App updated...")
    }

    componentDidMount () {
        console.log("Crossword Board App is Mounting...")
        window.addEventListener('resize', this.handleWindowResize)

    }

    componentWillUnmount () {
        console.log("Crossword Board App is Unmounting...")
        window.removeEventListener('resize', this.handleWindowResize)

        // save state of grid
    }

    handleWindowResize () {
        this.setState({
            windowWidth: window.innerWidth
        })
    }

    acrossClueClicked (clue) {
        console.log('Clicked down clue with number: ' + clue.number)
    }

    downClueClicked (clue) {
        console.log('Clicked down clue with number: ' + clue.number)
    }

    boardSquareClicked (square) {
        if (square.value === "_") {
            return
        }
        let selection = this.state.currentSelection
        if (square.rowCoord === selection.coords[0] && square.colCoord === selection.coords[1]) {
            selection.flipDirection()
        } else {
            selection.moveTo([square.rowCoord, square.colCoord])
        }
        this.setState({
            currentSelection: selection
        })
    }

    getSelectedSquares (selection) {
        const coords = selection.coords
        const direction = selection.direction
        let boardSquare = this.state.grid[coords[0]][coords[1]]
        let selectedCoordList = []
        if (direction === "Across") {
            let clue = this.acrossClues.find(clue => clue.number === boardSquare.acrossClueNum)
            let firstCoords = [clue.rowCoord, clue.colCoord]
            let wordLen = clue.answerLength
            for (let i = firstCoords[1]; i < firstCoords[1] + wordLen; i++) {
                selectedCoordList.push([firstCoords[0],i].toString())
            }
        } else {
            let clue = this.downClues.find(clue => clue.number === boardSquare.downClueNum)
            let firstCoords = [clue.rowCoord, clue.colCoord]
            let wordLen = clue.answerLength
            for (let i = firstCoords[0]; i < firstCoords[0] + wordLen; i++) {
                selectedCoordList.push([i,firstCoords[1]].toString())
            }
        }
        return selectedCoordList
    }

    getSquareBackgroundColor (square, selection, highlightedSquares) {
        if (square.value === "_") return "black"
        let thisSquareKey = [square.rowCoord, square.colCoord].toString()
        if (highlightedSquares.includes(thisSquareKey)) {
            if (thisSquareKey == selection.coords.toString()) {
                return this.props.settings.colorScheme.colors[1]
            } else {
                return this.props.settings.colorScheme.colors[2]
            }
        }
        return "white"
    }

    render () {
        const { windowWidth, currentSelection, grid, clueScrollTop } = this.state
        let clueWidthPx = "" + (windowWidth - 790 - 2) + "px"

        const boardPx = this.props.boardWidthPx
        const boardSize = grid.length
        const rowPx = boardPx / boardSize
        const squarePx = rowPx - 1

        const highlightedSquares = this.getSelectedSquares(currentSelection)

        const acrossClue = this.acrossClues.find(c => c.number === grid[currentSelection.coords[0]][currentSelection.coords[1]].acrossClueNum)
        const downClue = this.downClues.find(c => c.number === grid[currentSelection.coords[0]][currentSelection.coords[1]].downClueNum)



        /*const acrossClueObjs = document.getElementById("across-clue-scroll").getElementsByClassName("crossword-clue-row")
        const curAcrossClueNum = grid[currentSelection.coords[0]][currentSelection.coords[1]].acrossClueNum
        const curAcrossClueInd = this.acrossClues.findIndex(c => c.number === curAcrossClueNum)
        const acrossScrollTop = acrossClueObjs[curAcrossClueInd].offsetTop - acrossClueObjs[0].offsetTop

        const downClueObjs = document.getElementById("down-clue-scroll").getElementsByClassName("crossword-clue-row")
        const curDownClueNum = grid[currentSelection.coords[0]][currentSelection.coords[1]].downClueNum
        const curDownClueInd = this.downClues.findIndex(c => c.number === curDownClueNum)
        const downScrollTop = downClueObjs[curDownClueInd].offsetTop - downClueObjs[0].offsetTop*/

        return (
            <Fragment>
                <div className="crossword-board-and-clues-wrapper">
                    <div className="crossword-board" style={{ width : boardPx, height : boardPx }}>
                        {grid.map( (row, i) =>
                            <div key={i} style={{height : rowPx}}>
                                {row.map( (square, j) => 
                                    <div key={j} className={"crossword-square"} onClick={() => { this.boardSquareClicked(square) }}
                                            style={{width : squarePx, height : squarePx, 
                                                backgroundColor : this.getSquareBackgroundColor(square, currentSelection, highlightedSquares)}}>
                                        <div className="board-square-number" 
                                            style={{fontSize: `${boardSize > 15 ? "6pt" : `${boardSize > 10 ? "9pt" : "12pt"}`}`}}>
                                            {this.numbered[i][j] != "_" && this.numbered[i][j] != "0" ? this.numbered[i][j] : ""}</div>
                                        {square.value === '?' || square.value === '_' ? '' : square.value}
                                    </div>
                                )}
                            </div>

                        )}
                    </div>
                    <div className="crossword-clue-section" style={{width : clueWidthPx, height : boardPx}}>
                        <CrosswordClueScroll 
                            listTitle={"Across"}
                            clueList={this.acrossClues}
                            selectedClue={acrossClue}
                            isMatchingDirection={currentSelection.direction === 'Across'}
                            scrollTop={clueScrollTop}
                            handleScroll={this.handleClueScroll}
                            clueClicked={this.acrossClueClicked}
                            settings={this.props.settings}/>
                        <CrosswordClueScroll 
                            listTitle={"Down"}
                            clueList={this.downClues}
                            selectedClue={downClue}
                            isMatchingDirection={currentSelection.direction === 'Down'}
                            scrollTop={clueScrollTop}
                            handleScroll={this.handleClueScroll}
                            clueClicked={this.downClueClicked}
                            settings={this.props.settings}/>
                    </div>
                </div>
            </Fragment>
        )
    }
}

CrosswordBoardApp.propTypes = {
    crossword: PropTypes.object.isRequired,
    boardWidthPx: PropTypes.number.isRequired,
    settings: PropTypes.object.isRequired
}

export default CrosswordBoardApp
