class SquareSelection {
    constructor () {
        this.coords = [0,0]
        this.direction = "Across"
    }

    flipDirection () {
        this.direction === "Across" ? this.direction = "Down" : this.direction = "Across"
    }

    moveTo (coords) {
        this.coords = coords
    }

    moveToWithDirection (coords, direction) {
        this.coords = coords
        this.direction = direction
    }

    moveRight () {
        this.coords = [this.coords[0], this.coords[1] + 1]
    }

    moveLeft () {
        this.coords = [this.coords[0], this.coords[1] - 1]
    }

    moveUp () {
        this.coords = [this.coords[0] - 1, this.coords[1]]
    }

    moveDown () {
        this.coords = [this.coords[0] + 1, this.coords[1]]
    }
}

export default SquareSelection
