import React, { Component } from 'react'
import PropTypes from 'prop-types'
import '../css/MessageModal.css'

class MessageModal extends Component {

    render () {
        console.log(this.props.message)
        return (
            <div style={{display: `${this.props.shouldShow ? "block" : "none"}`}} className="message-modal-wrapper">
                <div className="message-modal-body">
                    {this.props.message}
                </div>
                <div className="message-modal-footer">
                    <div className="message-modal-button" onClick={() => this.props.buttonAction1()}>{this.props.buttonText1}</div>
                    {this.props.buttonText2 ? <div className="message-modal-button" 
                        onClick={() => this.props.buttonAction2()}>{this.props.buttonText2}</div> : null}
                </div>
            </div>

        )
    }
}

MessageModal.propTypes = {
    shouldShow: PropTypes.bool.isRequired,
    message: PropTypes.string.isRequired,
    buttonText1: PropTypes.string.isRequired,
    buttonAction1: PropTypes.func.isRequired,
    buttonText2: PropTypes.string,
    buttonAction2: PropTypes.string
}

export default MessageModal
